advancedQueries.query:
consul_consul_cache_fetch_success{kube_cluster_name=~$cluster, kube_namespace_name=~$namespace, kube_pod_name=~$pod}
/
(
consul_consul_cache_fetch_success{kube_cluster_name=~$cluster, kube_namespace_name=~$namespace, kube_pod_name=~$pod}
+ ignoring(result_not_modified) group_left
consul_consul_cache_fetch_error{kube_cluster_name=~$cluster, kube_namespace_name=~$namespace, kube_pod_name=~$pod}
)

consul_consul_cache_evict_expired{kube_cluster_name=~$cluster, kube_namespace_name=~$namespace, kube_pod_name=~$pod}
/
(
consul_consul_cache_evict_expired{kube_cluster_name=~$cluster, kube_namespace_name=~$namespace, kube_pod_name=~$pod}
+ 
consul_consul_cache_entries_count{kube_cluster_name=~$cluster, kube_namespace_name=~$namespace, kube_pod_name=~$pod}
)

consul_consul_cache_bypass{kube_cluster_name=~$cluster, kube_namespace_name=~$namespace, kube_pod_name=~$pod}
/
(
  (
	consul_consul_cache_bypass{kube_cluster_name=~$cluster, kube_namespace_name=~$namespace, kube_pod_name=~$pod}
    +
	consul_consul_cache_fetch_error{kube_cluster_name=~$cluster, kube_namespace_name=~$namespace, kube_pod_name=~$pod}
  )
  + ignoring(result_not_modified) group_right
  consul_consul_cache_fetch_success{kube_cluster_name=~$cluster, kube_namespace_name=~$namespace, kube_pod_name=~$pod}
)
